import { useState } from "react";

function App() {
  const days = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница"];

  // Меню на каждый день: 3 блюда
  const fullMenu = {
    Понедельник: [
      { name: "Суп овощной" },
      { name: "Котлета с гарниром" },
      { name: "Компот" },
    ],
    Вторник: [
      { name: "Борщ" },
      { name: "Курица с рисом" },
      { name: "Компот" },
    ],
    Среда: [
      { name: "Суп-пюре из тыквы" },
      { name: "Паста с мясом" },
      { name: "Компот" },
    ],
    Четверг: [
      { name: "Овсянка" },
      { name: "Котлета с картошкой" },
      { name: "Компот" },
    ],
    Пятница: [
      { name: "Яичница с овощами" },
      { name: "Рыба с гарниром" },
      { name: "Компот" },
    ],
  };

  // --- State ---
  const [user, setUser] = useState(null);
  const [selectedDay, setSelectedDay] = useState(days[0]);
  const [payments, setPayments] = useState({});
  const [received, setReceived] = useState({});
  const [issuedMeals, setIssuedMeals] = useState({});
  const [requests, setRequests] = useState([]);
  const [approvedRequests, setApprovedRequests] = useState([]);
  const [stats, setStats] = useState({ totalPayments: 0, totalMeals: 0 });
  const [reviews, setReviews] = useState([]);
  const [allergies, setAllergies] = useState([]);

  const [newReview, setNewReview] = useState("");
  const [newAllergy, setNewAllergy] = useState("");
  const [newRequest, setNewRequest] = useState({ product: "", quantity: 0 });

  // --- Actions ---
  const payMeal = (type) => {
    setPayments({ ...payments, [selectedDay]: type });
    setReceived({ ...received, [selectedDay]: true });
    setStats({ ...stats, totalPayments: stats.totalPayments + 1, totalMeals: stats.totalMeals + 1 });
  };

  const issueMeal = (type) => {
    setIssuedMeals({ ...issuedMeals, [type]: (issuedMeals[type] || 0) + 1 });
    setStats({ ...stats, totalMeals: stats.totalMeals + 1 });
  };

  const addReview = () => {
    if (newReview.trim()) {
      setReviews([...reviews, newReview]);
      setNewReview("");
    }
  };

  const addAllergy = () => {
    if (newAllergy.trim()) {
      setAllergies([...allergies, newAllergy]);
      setNewAllergy("");
    }
  };

  const submitRequest = () => {
    if (newRequest.product && newRequest.quantity > 0) {
      setRequests([...requests, { ...newRequest, approved: false }]);
      setNewRequest({ product: "", quantity: 0 });
    }
  };

  const approveRequest = (index) => {
    const newReqs = [...requests];
    newReqs[index].approved = true;
    setApprovedRequests([...approvedRequests, newReqs[index]]);
    setRequests(newReqs.filter((_, i) => i !== index));
  };

  // --- Styles (Тема) ---
  const styles = {
    app: { fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", background: "#f7f9fc", minHeight: "100vh", padding: 20 },
    card: { background: "#fff", borderRadius: 12, padding: 25, marginBottom: 25, boxShadow: "0 6px 18px rgba(0,0,0,0.1)", transition: "0.3s", hover: { transform: "scale(1.02)" } },
    button: { padding: "10px 20px", margin: "5px", border: "none", borderRadius: 6, cursor: "pointer", transition: "0.3s", fontWeight: "bold" },
    buttonPrimary: { backgroundColor: "#4CAF50", color: "#fff" },
    buttonSecondary: { backgroundColor: "#2196F3", color: "#fff" },
    buttonDanger: { backgroundColor: "#f44336", color: "#fff" },
    input: { padding: "8px 12px", borderRadius: 6, border: "1px solid #ccc", marginBottom: 10, width: "100%" },
    select: { padding: "8px 12px", borderRadius: 6, marginBottom: 15, width: "100%" },
    cardMenu: { padding: 15, borderRadius: 8, margin: "10px 0", background: "#e9f0f7", boxShadow: "0 2px 5px rgba(0,0,0,0.05)", transition: "0.3s" },
    notification: { position: "fixed", top: 20, right: 20, background: "#4CAF50", color: "#fff", padding: "10px 20px", borderRadius: 8, boxShadow: "0 2px 10px rgba(0,0,0,0.2)", zIndex: 1000, animation: "fadein 0.5s, fadeout 0.5s 2.5s" },
  };

  return (
    <div style={styles.app}>
      <h1 style={{ textAlign:"center", marginBottom:30, color:"#333" }}>Школьная еда</h1>

      {/* --- Login --- */}
      {!user && (
        <div style={styles.card}>
          <h2>Вход</h2>
          <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>setUser({role:"ученик", name:"Ученик"})}>Войти как ученик</button>
          <button style={{...styles.button, ...styles.buttonSecondary}} onClick={()=>setUser({role:"повар", name:"Повар"})}>Войти как повар</button>
          <button style={{...styles.button, ...styles.buttonDanger}} onClick={()=>setUser({role:"админ", name:"Админ"})}>Войти как админ</button>
        </div>
      )}

      {/* --- Ученик --- */}
      {user && user.role === "ученик" && (
        <div style={styles.card}>
          <h2>Ученик: {user.name}</h2>
          <button style={styles.button} onClick={()=>setUser(null)}>Назад</button>

          <h3>Выберите день недели</h3>
          <select style={styles.select} value={selectedDay} onChange={(e)=>setSelectedDay(e.target.value)}>
            {days.map(day => <option key={day} value={day}>{day}</option>)}
          </select>

          <h3>Меню {selectedDay}</h3>
          {fullMenu[selectedDay].map((item, i) => (
            <div style={styles.cardMenu} key={i}>{item.name}</div>
          ))}

          <h3>Оплата</h3>
          {!payments[selectedDay] ? (
            <>
              <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>payMeal("разово")}>Разово</button>
              <button style={{...styles.button, ...styles.buttonSecondary}} onClick={()=>payMeal("абонемент")}>Абонемент</button>
            </>
          ) : (
            <p style={{color:"green", fontWeight:"bold"}}>✅ Питание получено</p>
          )}

          <h3>Пищевые аллергии</h3>
          <input style={styles.input} placeholder="Добавить аллергию" value={newAllergy} onChange={(e)=>setNewAllergy(e.target.value)} />
          <button style={{...styles.button, ...styles.buttonPrimary}} onClick={addAllergy}>Добавить</button>
          <ul>{allergies.map((a,i)=><li key={i}>{a}</li>)}</ul>

          <h3>Отзывы о блюдах</h3>
          <input style={styles.input} placeholder="Ваш отзыв" value={newReview} onChange={(e)=>setNewReview(e.target.value)} />
          <button style={{...styles.button, ...styles.buttonSecondary}} onClick={addReview}>Добавить</button>
          <ul>{reviews.map((r,i)=><li key={i}>{r}</li>)}</ul>
        </div>
      )}

      {/* --- Повар --- */}
      {user && user.role === "повар" && (
        <div style={styles.card}>
          <h2>Повар: {user.name}</h2>
          <button style={styles.button} onClick={()=>setUser(null)}>Назад</button>

          <h3>Учёт выданных блюд</h3>
          <p>Завтрак: {issuedMeals["Завтрак"] || 0}</p>
          <p>Обед: {issuedMeals["Обед"] || 0}</p>
          <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>issueMeal("Завтрак")}>Выдать завтрак</button>
          <button style={{...styles.button, ...styles.buttonSecondary}} onClick={()=>issueMeal("Обед")}>Выдать обед</button>

          <h3>Контроль остатков и заявки на продукты</h3>
          <input style={styles.input} placeholder="Продукт" value={newRequest.product} onChange={e=>setNewRequest({...newRequest, product:e.target.value})} />
          <input style={styles.input} type="number" placeholder="Количество" value={newRequest.quantity} onChange={e=>setNewRequest({...newRequest, quantity:Number(e.target.value)})} />
          <button style={{...styles.button, ...styles.buttonPrimary}} onClick={submitRequest}>Создать заявку</button>
          <ul>
            {requests.map((r,i)=><li key={i}>{r.product} - {r.quantity}</li>)}
          </ul>
        </div>
      )}

      {/* --- Админ --- */}
      {user && user.role === "админ" && (
        <div style={styles.card}>
          <h2>Админ: {user.name}</h2>
          <button style={styles.button} onClick={()=>setUser(null)}>Назад</button>

          <h3>Статистика</h3>
          <p>Всего оплат: {stats.totalPayments}</p>
          <p>Всего выдано блюд: {stats.totalMeals}</p>

          <h3>Согласование заявок</h3>
          <ul>
            {requests.map((r,i)=>(
              <li key={i} style={{display:"flex", justifyContent:"space-between", alignItems:"center", padding:"5px 0"}}>
                <span>{r.product} - {r.quantity} (Ожидает)</span>
                <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>approveRequest(i)}>Согласовать</button>
              </li>
            ))}
          </ul>

          <h3>Сформированные отчёты</h3>
          <ul>{approvedRequests.map((r,i)=><li key={i}>{r.product} - {r.quantity}</li>)}</ul>
        </div>
      )}

      {/* --- Анимации --- */}
      <style>{`
        .card:hover { transform: scale(1.02); }
        .cardMenu:hover { transform: scale(1.02); background: #d9eaf7; }
        button:hover { opacity: 0.85; }
        @keyframes fadein { from {opacity:0; transform:translateY(-20px);} to {opacity:1; transform:translateY(0);} }
        @keyframes fadeout { from {opacity:1;} to {opacity:0;} }
      `}</style>
    </div>
  );
}

export default App;
