import { useState } from "react";

function App() {
  const days = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница"];

  const fullMenu = {
    Понедельник: [
      { type: "Завтрак", name: "Овсянка с фруктами" },
      { type: "Обед", name: "Куриный суп с лапшой" },
    ],
    Вторник: [
      { type: "Завтрак", name: "Яичница с овощами" },
      { type: "Обед", name: "Гречка с котлетой" },
    ],
    Среда: [
      { type: "Завтрак", name: "Блинчики с творогом" },
      { type: "Обед", name: "Суп-пюре из тыквы" },
    ],
    Четверг: [
      { type: "Завтрак", name: "Мюсли с йогуртом" },
      { type: "Обед", name: "Паста с курицей" },
    ],
    Пятница: [
      { type: "Завтрак", name: "Омлет с зеленью" },
      { type: "Обед", name: "Рыба с овощами" },
    ],
  };

  const [user, setUser] = useState(null);
  const [selectedDay, setSelectedDay] = useState(days[0]);
  const [allergies, setAllergies] = useState("");
  const [reviews, setReviews] = useState([]);

  const handleLogin = (role) => {
    const name = prompt(`Введите имя для роли ${role}`);
    if (name) setUser({ name, role });
  };

  const handleBack = () => setUser(null);

  const handlePay = () => alert("Оплата прошла успешно!");
  const handleReceive = () => alert("Питание отмечено как полученное!");
  const handleCreateRequest = () => alert("Заявка создана!");
  const handleApproveRequest = () => alert("Заявка согласована!");
  const handleAddAllergy = () => {
    const allergy = prompt("Введите ваши пищевые аллергии или предпочтения");
    if (allergy) setAllergies(allergy);
  };
  const handleAddReview = () => {
    const review = prompt("Ваш отзыв о блюде:");
    if (review) setReviews([...reviews, review]);
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>SchoolFood</h1>

      {!user && (
        <div>
          <h2>Вход</h2>
          <button onClick={() => handleLogin("ученик")}>Войти как ученик</button>
          <button onClick={() => handleLogin("повар")}>Войти как повар</button>
          <button onClick={() => handleLogin("администратор")}>Войти как админ</button>
        </div>
      )}

      {user && user.role === "ученик" && (
        <div>
          <h2>Ученик: {user.name}</h2>
          <button onClick={handleBack}>Назад</button>

          <h3>Выберите день недели</h3>
          <select
            value={selectedDay}
            onChange={(e) => setSelectedDay(e.target.value)}
          >
            {days.map((day) => (
              <option key={day} value={day}>
                {day}
              </option>
            ))}
          </select>

          <h3>Меню {selectedDay}</h3>
          <ul>
            {fullMenu[selectedDay].map((item, i) => (
              <li key={i}>
                {item.type}: {item.name}
              </li>
            ))}
          </ul>

          <button onClick={handlePay}>Оплатить питание</button>
          <button onClick={handleReceive}>Получить питание</button>
          <button onClick={handleAddAllergy}>Добавить аллергии/предпочтения</button>
          <button onClick={handleAddReview}>Оставить отзыв</button>

          {allergies && <p>Аллергии/предпочтения: {allergies}</p>}
          {reviews.length > 0 && (
            <div>
              <h3>Отзывы учеников</h3>
              <ul>
                {reviews.map((r, i) => (
                  <li key={i}>{r}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      {user && user.role === "повар" && (
        <div>
          <h2>Повар: {user.name}</h2>
          <button onClick={handleBack}>Назад</button>
          <button onClick={handleCreateRequest}>Создать заявку</button>
          <p>Учёт выдачи завтраков и обедов пока не реализован</p>
        </div>
      )}

      {user && user.role === "администратор" && (
        <div>
          <h2>Администратор: {user.name}</h2>
          <button onClick={handleBack}>Назад</button>
          <button onClick={handleApproveRequest}>Согласовать заявку</button>
          <p>Просмотр статистики и отчётов пока не реализован</p>
        </div>
      )}
    </div>
  );
}

export default App;
