import { useState, useEffect } from "react";

function App() {
  const days = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница"];

  const fullMenu = {
    Понедельник: [
      { type: "Завтрак", name: "Овсянка с фруктами" },
      { type: "Обед", name: "Куриный суп с лапшой" },
    ],
    Вторник: [
      { type: "Завтрак", name: "Яичница с овощами" },
      { type: "Обед", name: "Гречка с котлетой" },
    ],
    Среда: [
      { type: "Завтрак", name: "Блинчики с творогом" },
      { type: "Обед", name: "Суп-пюре из тыквы" },
    ],
    Четверг: [
      { type: "Завтрак", name: "Мюсли с йогуртом" },
      { type: "Обед", name: "Паста с курицей" },
    ],
    Пятница: [
      { type: "Завтрак", name: "Омлет с зеленью" },
      { type: "Обед", name: "Рыба с овощами" },
    ],
  };

  // --- State ---
  const [user, setUser] = useState(null);
  const [selectedDay, setSelectedDay] = useState(days[0]);
  const [allergies, setAllergies] = useState("");
  const [reviews, setReviews] = useState([]);
  const [payments, setPayments] = useState({});
  const [received, setReceived] = useState({});
  const [issuedMeals, setIssuedMeals] = useState({});
  const [stock, setStock] = useState({ "Завтрак": 10, "Обед": 10 });
  const [requests, setRequests] = useState([]);
  const [approvedRequests, setApprovedRequests] = useState([]);
  const [stats, setStats] = useState({ totalPayments: 0, totalMeals: 0 });
  const [notification, setNotification] = useState(null);

  // --- Login / Logout ---
  const handleLogin = (role) => {
    const name = prompt(`Введите имя для роли ${role}`);
    if (name) setUser({ name, role });
  };
  const handleBack = () => setUser(null);

  // --- Notifications ---
  const showNotification = (msg) => {
    setNotification(msg);
    setTimeout(() => setNotification(null), 2000);
  };

  // --- Ученик ---
  const handlePayment = (type) => {
    setPayments({ ...payments, [selectedDay]: type });
    setReceived({ ...received, [selectedDay]: true });
    setStats({
      ...stats,
      totalPayments: stats.totalPayments + 1,
      totalMeals: stats.totalMeals + 1,
    });
    showNotification(`Оплата (${type}) прошла успешно! Питание отмечено как полученное.`);
  };
  const handleAddAllergy = () => {
    const allergy = prompt("Введите ваши пищевые аллергии или предпочтения");
    if (allergy) {
      setAllergies(allergy);
      showNotification("Аллергии/предпочтения добавлены");
    }
  };
  const handleAddReview = () => {
    const review = prompt("Ваш отзыв о блюде:");
    if (review) {
      setReviews([...reviews, review]);
      showNotification("Отзыв добавлен");
    }
  };

  // --- Повар ---
  const handleIssueMeal = (type) => {
    if (stock[type] <= 0) return showNotification("Нет остатков продукта!");
    setIssuedMeals({ ...issuedMeals, [type]: (issuedMeals[type] || 0) + 1 });
    setStock({ ...stock, [type]: stock[type] - 1 });
    setStats({ ...stats, totalMeals: stats.totalMeals + 1 });
    showNotification(`${type} выдан`);
  };
  const handleAddStockRequest = () => {
    const product = prompt("Введите продукт для закупки:");
    const quantity = prompt("Введите количество:");
    if (product && quantity) {
      setRequests([...requests, { product, quantity, approved: false }]);
      showNotification("Заявка на закупку создана");
    }
  };

  // --- Админ ---
  const handleApproveRequest = (index) => {
    const newRequests = [...requests];
    newRequests[index].approved = true;
    setApprovedRequests([...approvedRequests, newRequests[index]]);
    setRequests(newRequests.filter((_, i) => i !== index));
    showNotification("Заявка согласована");
  };

  // --- Styles ---
  const styles = {
    app: { fontFamily: "Arial, sans-serif", background: "#f0f2f5", minHeight: "100vh", padding: 20 },
    card: { background: "#fff", borderRadius: 10, padding: 25, marginBottom: 25, boxShadow: "0 4px 12px rgba(0,0,0,0.1)", transition: "0.3s", hover: { transform: "scale(1.02)" } },
    sectionTitle: { borderBottom: "2px solid #eee", paddingBottom: 5, marginBottom: 15 },
    button: { padding: "10px 20px", margin: "5px", border: "none", borderRadius: 6, cursor: "pointer", transition: "0.3s" },
    buttonPrimary: { backgroundColor: "#4CAF50", color: "#fff" },
    buttonSecondary: { backgroundColor: "#2196F3", color: "#fff" },
    buttonDanger: { backgroundColor: "#f44336", color: "#fff" },
    select: { padding: "8px 12px", borderRadius: 5, marginBottom: 15 },
    cardMenu: { padding: 15, borderRadius: 8, margin: "10px 0", background: "#f7f7f7", display: "flex", justifyContent: "space-between", alignItems: "center", transition: "0.2s", cursor: "pointer" },
    breakfastTag: { backgroundColor: "#FFEB3B", color: "#333", padding: "3px 8px", borderRadius: 5, fontWeight: "bold" },
    lunchTag: { backgroundColor: "#FF9800", color: "#fff", padding: "3px 8px", borderRadius: 5, fontWeight: "bold" },
    notification: { position: "fixed", top: 20, right: 20, background: "#4CAF50", color: "#fff", padding: "10px 20px", borderRadius: 8, boxShadow: "0 2px 10px rgba(0,0,0,0.2)", zIndex: 1000, animation: "fadein 0.5s, fadeout 0.5s 1.5s" }
  };

  return (
    <div style={styles.app}>
      <h1 style={{ textAlign: "center", marginBottom: 30 }}>SchoolFood</h1>

      {notification && <div style={styles.notification}>{notification}</div>}

      {/* --- Login --- */}
      {!user && (
        <div style={styles.card}>
          <h2 style={styles.sectionTitle}>Вход</h2>
          <button style={{ ...styles.button, ...styles.buttonPrimary }} onClick={() => handleLogin("ученик")}>Войти как ученик</button>
          <button style={{ ...styles.button, ...styles.buttonSecondary }} onClick={() => handleLogin("повар")}>Войти как повар</button>
          <button style={{ ...styles.button, ...styles.buttonDanger }} onClick={() => handleLogin("администратор")}>Войти как админ</button>
        </div>
      )}

      {/* --- Ученик --- */}
      {user && user.role === "ученик" && (
        <div style={styles.card}>
          <h2 style={styles.sectionTitle}>Ученик: {user.name}</h2>
          <button style={styles.button} onClick={handleBack}>Назад</button>

          <h3>Выберите день недели</h3>
          <select style={styles.select} value={selectedDay} onChange={(e) => setSelectedDay(e.target.value)}>
            {days.map(day => <option key={day} value={day}>{day}</option>)}
          </select>

          <h3>Меню {selectedDay}</h3>
          {fullMenu[selectedDay].map((item, i) => (
            <div style={styles.cardMenu} key={i}>
              <span>{item.name}</span>
              <span style={item.type === "Завтрак" ? styles.breakfastTag : styles.lunchTag}>{item.type}</span>
            </div>
          ))}

          {!payments[selectedDay] && (
            <div>
              <button style={{ ...styles.button, ...styles.buttonPrimary }} onClick={() => handlePayment("разово")}>Оплатить разово</button>
              <button style={{ ...styles.button, ...styles.buttonSecondary }} onClick={() => handlePayment("абонемент")}>Оплатить по абонементу</button>
            </div>
          )}

          {received[selectedDay] && <p style={{ color: "green", fontWeight: "bold", marginTop: 10 }}>✅ Питание получено</p>}

          <button style={styles.button} onClick={handleAddAllergy}>Добавить аллергии/предпочтения</button>
          <button style={styles.button} onClick={handleAddReview}>Оставить отзыв</button>

          {allergies && <p>Аллергии/предпочтения: {allergies}</p>}
          {reviews.length > 0 && (
            <div>
              <h3>Отзывы учеников</h3>
              <ul>{reviews.map((r,i) => <li key={i}>{r}</li>)}</ul>
            </div>
          )}
        </div>
      )}

      {/* --- Повар --- */}
      {user && user.role === "повар" && (
        <div style={styles.card}>
          <h2 style={styles.sectionTitle}>Повар: {user.name}</h2>
          <button style={styles.button} onClick={handleBack}>Назад</button>

          <h3>Выданные блюда</h3>
          <p>Завтрак: {issuedMeals["Завтрак"] || 0}</p>
          <p>Обед: {issuedMeals["Обед"] || 0}</p>
          <button style={{ ...styles.button, ...styles.buttonPrimary }} onClick={() => handleIssueMeal("Завтрак")}>Выдать завтрак</button>
          <button style={{ ...styles.button, ...styles.buttonSecondary }} onClick={() => handleIssueMeal("Обед")}>Выдать обед</button>

          <h3>Остатки продуктов</h3>
          <p>Завтрак: {stock["Завтрак"]}</p>
          <p>Обед: {stock["Обед"]}</p>

          <h3>Заявки на закупку</h3>
          <button style={styles.button} onClick={handleAddStockRequest}>Создать заявку</button>
          <ul>{requests.map((r,i) => <li key={i}>{r.product} - {r.quantity}</li>)}</ul>
        </div>
      )}

      {/* --- Админ --- */}
      {user && user.role === "администратор" && (
        <div style={styles.card}>
          <h2 style={styles.sectionTitle}>Администратор: {user.name}</h2>
          <button style={styles.button} onClick={handleBack}>Назад</button>

          <h3>Статистика</h3>
          <p>Всего оплат: {stats.totalPayments}</p>
          <p>Всего выдано блюд: {stats.totalMeals}</p>

          <h3>Согласование заявок</h3>
          <ul>
            {requests.map((r,i) => (
              <li key={i} style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "5px 0" }}>
                <span>{r.product} - {r.quantity} (Ожидает)</span>
                <button style={{ ...styles.button, ...styles.buttonPrimary }} onClick={() => handleApproveRequest(i)}>Согласовать</button>
              </li>
            ))}
          </ul>

          <h3>Сформированные отчёты</h3>
          <ul>{approvedRequests.map((r,i) => <li key={i}>{r.product} - {r.quantity}</li>)}</ul>
        </div>
      )}

      {/* --- Анимация keyframes для уведомления --- */}
      <style>
        {`
          @keyframes fadein {
            from { opacity: 0; transform: translateY(-20px); }
            to { opacity: 1; transform: translateY(0); }
          }
          @keyframes fadeout {
            from { opacity: 1; }
            to { opacity: 0; }
          }
        `}
      </style>
    </div>
  );
}

export default App;
