import { useState } from "react";

function App() {
  const days = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница"];

  // Меню на каждый день: 3 блюда
  const fullMenu = {
    Понедельник: [
      { name: "Суп овощной" },
      { name: "Котлета с гарниром" },
      { name: "Компот" },
    ],
    Вторник: [
      { name: "Борщ" },
      { name: "Курица с рисом" },
      { name: "Компот" },
    ],
    Среда: [
      { name: "Суп-пюре из тыквы" },
      { name: "Паста с мясом" },
      { name: "Компот" },
    ],
    Четверг: [
      { name: "Овсянка" },
      { name: "Котлета с картошкой" },
      { name: "Компот" },
    ],
    Пятница: [
      { name: "Яичница с овощами" },
      { name: "Рыба с гарниром" },
      { name: "Компот" },
    ],
  };

  // --- State ---
  const [user, setUser] = useState(null);
  const [selectedDay, setSelectedDay] = useState(days[0]);
  const [notification, setNotification] = useState(null);
  const [modal, setModal] = useState({ visible: false, message: "", onConfirm: null });
  const [payments, setPayments] = useState({});
  const [received, setReceived] = useState({});
  const [issuedMeals, setIssuedMeals] = useState({});
  const [requests, setRequests] = useState([]);
  const [approvedRequests, setApprovedRequests] = useState([]);
  const [stats, setStats] = useState({ totalPayments: 0, totalMeals: 0 });

  // --- Notifications ---
  const showNotification = (msg) => {
    setNotification(msg);
    setTimeout(() => setNotification(null), 3000); // дольше показываем уведомление
  };

  // --- Модальное окно ---
  const openModal = (message, onConfirm) => {
    setModal({ visible: true, message, onConfirm });
  };
  const confirmModal = () => {
    if (modal.onConfirm) modal.onConfirm();
    setModal({ visible: false, message: "", onConfirm: null });
  };
  const cancelModal = () => setModal({ visible: false, message: "", onConfirm: null });

  // --- Ученик ---
  const handlePayment = (type) => {
    openModal(`Вы собираетесь оплатить питание (${type})`, () => {
      setPayments({ ...payments, [selectedDay]: type });
      setReceived({ ...received, [selectedDay]: true });
      setStats({ ...stats, totalPayments: stats.totalPayments + 1, totalMeals: stats.totalMeals + 1 });
      showNotification(`Оплата (${type}) проведена! Питание отмечено как полученное.`);
    });
  };

  // --- Повар ---
  const handleIssueMeal = (type) => {
    openModal(`${type} будет выдан`, () => {
      setIssuedMeals({ ...issuedMeals, [type]: (issuedMeals[type] || 0) + 1 });
      showNotification(`${type} выдан`);
      setStats({ ...stats, totalMeals: stats.totalMeals + 1 });
    });
  };

  // --- Админ ---
  const handleApproveRequest = (index) => {
    openModal("Согласовать эту заявку?", () => {
      const newRequests = [...requests];
      newRequests[index].approved = true;
      setApprovedRequests([...approvedRequests, newRequests[index]]);
      setRequests(newRequests.filter((_, i) => i !== index));
      showNotification("Заявка согласована");
    });
  };

  // --- Styles ---
  const styles = {
    app: { fontFamily: "Arial, sans-serif", background: "#f0f2f5", minHeight: "100vh", padding: 20 },
    card: { background: "#fff", borderRadius: 10, padding: 25, marginBottom: 25, boxShadow: "0 4px 12px rgba(0,0,0,0.1)" },
    button: { padding: "10px 20px", margin: "5px", border: "none", borderRadius: 6, cursor: "pointer", transition: "0.3s" },
    buttonPrimary: { backgroundColor: "#4CAF50", color: "#fff" },
    buttonSecondary: { backgroundColor: "#2196F3", color: "#fff" },
    buttonDanger: { backgroundColor: "#f44336", color: "#fff" },
    select: { padding: "8px 12px", borderRadius: 5, marginBottom: 15 },
    cardMenu: { padding: 15, borderRadius: 8, margin: "10px 0", background: "#f7f7f7" },
    notification: { position: "fixed", top: 20, right: 20, background: "#4CAF50", color: "#fff", padding: "10px 20px", borderRadius: 8, boxShadow: "0 2px 10px rgba(0,0,0,0.2)", zIndex: 1000, animation: "fadein 0.5s, fadeout 0.5s 2.5s" },
    modalOverlay: { position: "fixed", top:0, left:0, right:0, bottom:0, backgroundColor:"rgba(0,0,0,0.5)", display:"flex", justifyContent:"center", alignItems:"center", zIndex: 2000 },
    modalContent: { background:"#fff", padding:30, borderRadius:10, textAlign:"center", minWidth:300 }
  };

  return (
    <div style={styles.app}>
      <h1 style={{ textAlign:"center", marginBottom:30 }}>Школьная еда</h1>

      {notification && <div style={styles.notification}>{notification}</div>}

      {/* --- Login --- */}
      {!user && (
        <div style={styles.card}>
          <h2>Вход</h2>
          <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>setUser({role:"ученик", name:"Ученик"})}>Войти как ученик</button>
          <button style={{...styles.button, ...styles.buttonSecondary}} onClick={()=>setUser({role:"повар", name:"Повар"})}>Войти как повар</button>
          <button style={{...styles.button, ...styles.buttonDanger}} onClick={()=>setUser({role:"админ", name:"Админ"})}>Войти как админ</button>
        </div>
      )}

      {/* --- Ученик --- */}
      {user && user.role === "ученик" && (
        <div style={styles.card}>
          <h2>Ученик: {user.name}</h2>
          <button style={styles.button} onClick={()=>setUser(null)}>Назад</button>

          <h3>Выберите день недели</h3>
          <select style={styles.select} value={selectedDay} onChange={(e)=>setSelectedDay(e.target.value)}>
            {days.map(day => <option key={day} value={day}>{day}</option>)}
          </select>

          <h3>Меню {selectedDay}</h3>
          {fullMenu[selectedDay].map((item, i) => (
            <div style={styles.cardMenu} key={i}>{item.name}</div>
          ))}

          {!payments[selectedDay] && (
            <div>
              <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>handlePayment("разово")}>Оплатить разово</button>
              <button style={{...styles.button, ...styles.buttonSecondary}} onClick={()=>handlePayment("абонемент")}>Оплатить по абонементу</button>
            </div>
          )}

          {received[selectedDay] && <p style={{color:"green", fontWeight:"bold", marginTop:10}}>✅ Питание получено</p>}
        </div>
      )}

      {/* --- Повар --- */}
      {user && user.role === "повар" && (
        <div style={styles.card}>
          <h2>Повар: {user.name}</h2>
          <button style={styles.button} onClick={()=>setUser(null)}>Назад</button>

          <h3>Выданные блюда</h3>
          <p>Завтрак: {issuedMeals["Завтрак"] || 0}</p>
          <p>Обед: {issuedMeals["Обед"] || 0}</p>
          <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>handleIssueMeal("Завтрак")}>Выдать завтрак</button>
          <button style={{...styles.button, ...styles.buttonSecondary}} onClick={()=>handleIssueMeal("Обед")}>Выдать обед</button>
        </div>
      )}

      {/* --- Админ --- */}
      {user && user.role === "админ" && (
        <div style={styles.card}>
          <h2>Админ: {user.name}</h2>
          <button style={styles.button} onClick={()=>setUser(null)}>Назад</button>

          <h3>Статистика</h3>
          <p>Всего оплат: {stats.totalPayments}</p>
          <p>Всего выдано блюд: {stats.totalMeals}</p>

          <h3>Согласование заявок</h3>
          <ul>
            {requests.map((r,i)=>(
              <li key={i} style={{display:"flex", justifyContent:"space-between", alignItems:"center", padding:"5px 0"}}>
                <span>{r.product} - {r.quantity} (Ожидает)</span>
                <button style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>handleApproveRequest(i)}>Согласовать</button>
              </li>
            ))}
          </ul>

          <h3>Сформированные отчёты</h3>
          <ul>{approvedRequests.map((r,i)=><li key={i}>{r.product} - {r.quantity}</li>)}</ul>
        </div>
      )}

      {/* --- Модальное окно --- */}
      {modal.visible && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p>{modal.message}</p>
            <button style={{...styles.button, ...styles.buttonPrimary}} onClick={confirmModal}>Подтвердить</button>
            <button style={{...styles.button, ...styles.buttonSecondary}} onClick={cancelModal}>Отмена</button>
          </div>
        </div>
      )}

      {/* --- Анимации --- */}
      <style>{`
        @keyframes fadein { from {opacity:0; transform:translateY(-20px);} to {opacity:1; transform:translateY(0);} }
        @keyframes fadeout { from {opacity:1;} to {opacity:0;} }
      `}</style>
    </div>
  );
}

export default App;
