import { useState } from "react";
import { FaCoffee, FaUtensils, FaAppleAlt } from "react-icons/fa";

function App() {
  const days = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница"];

  const fullMenu = {
    Понедельник: {
      Завтрак: [
        { name: "Овсянка", icon: <FaCoffee /> },
        { name: "Яйцо всмятку", icon: <FaUtensils /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
      Обед: [
        { name: "Суп овощной", icon: <FaUtensils /> },
        { name: "Котлета с гарниром", icon: <FaUtensils /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
    },
    Вторник: {
      Завтрак: [
        { name: "Бутерброд с сыром", icon: <FaUtensils /> },
        { name: "Йогурт", icon: <FaAppleAlt /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
      Обед: [
        { name: "Борщ", icon: <FaUtensils /> },
        { name: "Курица с рисом", icon: <FaUtensils /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
    },
    Среда: {
      Завтрак: [
        { name: "Сырники", icon: <FaUtensils /> },
        { name: "Мёд", icon: <FaAppleAlt /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
      Обед: [
        { name: "Суп-пюре из тыквы", icon: <FaUtensils /> },
        { name: "Паста с мясом", icon: <FaUtensils /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
    },
    Четверг: {
      Завтрак: [
        { name: "Овсянка с ягодами", icon: <FaCoffee /> },
        { name: "Яйцо всмятку", icon: <FaUtensils /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
      Обед: [
        { name: "Котлета с картошкой", icon: <FaUtensils /> },
        { name: "Салат", icon: <FaAppleAlt /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
    },
    Пятница: {
      Завтрак: [
        { name: "Блинчики", icon: <FaUtensils /> },
        { name: "Сметана", icon: <FaAppleAlt /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
      Обед: [
        { name: "Рыба с гарниром", icon: <FaUtensils /> },
        { name: "Салат", icon: <FaAppleAlt /> },
        { name: "Компот", icon: <FaAppleAlt /> },
      ],
    },
  };

  const roles = [
    { role: "ученик", display: "Ученик", login: "1", password: "0000" },
    { role: "повар", display: "Повар", login: "2", password: "0000" },
    { role: "админ", display: "Админ", login: "3", password: "0000" },
  ];

  const [user, setUser] = useState(null);
  const [selectedRole, setSelectedRole] = useState(null);
  const [loginInput, setLoginInput] = useState("");
  const [passwordInput, setPasswordInput] = useState("");
  const [selectedDay, setSelectedDay] = useState(days[0]);
  const [payments, setPayments] = useState({});
  const [received, setReceived] = useState({});
  const [issuedMeals, setIssuedMeals] = useState({});
  const [requests, setRequests] = useState([]);
  const [approvedRequests, setApprovedRequests] = useState([]);
  const [stats, setStats] = useState({ totalPayments: 0, totalMeals: 0 });
  const [reviews, setReviews] = useState([]);
  const [allergies, setAllergies] = useState([]);
  const [newReview, setNewReview] = useState("");
  const [newAllergy, setNewAllergy] = useState("");
  const [newRequest, setNewRequest] = useState({ product: "", quantity: 0 });
  const [notification, setNotification] = useState(null);

  const showNotification = (msg) => {
    setNotification(msg);
    setTimeout(() => setNotification(null), 3000);
  };

  const handleLogin = () => {
    if (!selectedRole) return showNotification("Выберите роль");
    if (loginInput === selectedRole.login && passwordInput === selectedRole.password) {
      setUser({ role: selectedRole.role, name: selectedRole.display });
      setLoginInput("");
      setPasswordInput("");
      showNotification(`Вход выполнен как ${selectedRole.display}`);
    } else {
      showNotification("Неверный логин или пароль");
    }
  };

  const payMeal = (type, mealType) => {
    const key = `${selectedDay}-${mealType}`;
    setPayments({ ...payments, [key]: type });
    setReceived({ ...received, [key]: true });
    setStats({ ...stats, totalPayments: stats.totalPayments + 1, totalMeals: stats.totalMeals + 1 });
    showNotification(`Оплата ${mealType.toLowerCase()} (${type}) проведена`);
  };

  const issueMeal = (mealType) => {
    setIssuedMeals({ ...issuedMeals, [mealType]: (issuedMeals[mealType] || 0) + 1 });
    setStats({ ...stats, totalMeals: stats.totalMeals + 1 });
    showNotification(`${mealType} выдан`);
  };

  const submitRequest = () => {
    if (newRequest.product && newRequest.quantity > 0) {
      setRequests([...requests, { ...newRequest, approved: false }]);
      setNewRequest({ product: "", quantity: 0 });
      showNotification("Заявка создана");
    }
  };

  const approveRequest = (index) => {
    const newReqs = [...requests];
    newReqs[index].approved = true;
    setApprovedRequests([...approvedRequests, newReqs[index]]);
    setRequests(newReqs.filter((_, i) => i !== index));
    showNotification("Заявка согласована");
  };

  const styles = {
    app: { fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", background: "#f7f9fc", minHeight: "100vh", padding: 20 },
    card: { background: "#fff", borderRadius: 12, padding: 25, marginBottom: 25, boxShadow: "0 6px 18px rgba(0,0,0,0.1)", transition: "0.3s" },
    button: { padding: "10px 20px", margin: "5px", border: "none", borderRadius: 6, cursor: "pointer", transition: "0.3s", fontWeight: "bold" },
    buttonPrimary: { backgroundColor: "#4CAF50", color: "#fff" },
    buttonSecondary: { backgroundColor: "#2196F3", color: "#fff" },
    buttonDanger: { backgroundColor: "#f44336", color: "#fff" },
    input: { padding: "8px 12px", borderRadius: 6, border: "1px solid #ccc", marginBottom: 10, width: "100%" },
    select: { padding: "8px 12px", borderRadius: 6, marginBottom: 15, width: "100%" },
    cardMenu: { padding: 15, borderRadius: 8, margin: "10px 0", background: "#e9f0f7", boxShadow: "0 2px 5px rgba(0,0,0,0.05)", display:"flex", alignItems:"center", gap:10, transition:"0.3s" },
    notification: { position: "fixed", top: 20, right: 20, background: "#4CAF50", color: "#fff", padding: "10px 20px", borderRadius: 8, boxShadow: "0 2px 10px rgba(0,0,0,0.2)", zIndex: 1000, animation: "fadein 0.5s, fadeout 0.5s 2.5s" },
  };

  return (
    <div style={styles.app}>
      <h1 style={{ textAlign:"center", marginBottom:30, color:"#333" }}>Школьная еда</h1>
      {notification && <div style={styles.notification}>{notification}</div>}

      {!user && !selectedRole && (
        <div style={styles.card}>
          <h2>Выберите роль</h2>
          {roles.map(r=>(
            <button key={r.role} style={{...styles.button, ...styles.buttonPrimary}} onClick={()=>setSelectedRole(r)}>
              {r.display}
            </button>
          ))}
        </div>
      )}

      {!user && selectedRole && (
        <div style={styles.card}>
          <h2>Вход для {selectedRole.display}</h2>
          <input style={styles.input} placeholder="Логин" value={loginInput} onChange={e=>setLoginInput(e.target.value)}/>
          <input style={styles.input} type="password" placeholder="Пароль" value={passwordInput} onChange={e=>setPasswordInput(e.target.value)}/>
          <button style={{...styles.button, ...styles.buttonPrimary}} onClick={handleLogin}>Войти</button>
          <button style={{...styles.button, ...styles.buttonSecondary}} onClick={()=>setSelectedRole(null)}>Назад</button>
        </div>
      )}

      {/* Здесь можно вставить интерфейсы ученика, повара и админа, как в предыдущей версии, с карточками и анимациями */}

      <style>{`
        .card:hover { transform: scale(1.02); }
        .cardMenu:hover { transform: scale(1.02); background: #d9eaf7; }
        button:hover { opacity: 0.85; }
        @keyframes fadein { from {opacity:0; transform:translateY(-20px);} to {opacity:1; transform:translateY(0);} }
        @keyframes fadeout { from {opacity:1;} to {opacity:0;} }
      `}</style>
    </div>
  );
}

export default App;
